import { useState } from 'react';
import Modal from './Modal';
import Backdrop from './Backdrop';

function Wine(props) {
  const [ showModal, setModalIsOpen] = useState(false);
    
  function infoHandler() {
   setModalIsOpen(true);
  }

  return (
    <div className="card">
      <h2>{props.name}</h2>
      <div className="actions">
        <button className="btn" onClick={infoHandler}>Info</button>
      </div>
      { showModal && <Modal name={props.name}/> }
      { showModal && <Backdrop onClose={closeInfoHandler}/> }
    </div>
  );
}

export default Wine;
